package PackageTask;//В приведенном ниже примере демонстрируется запуск текстового редактора Notepad в Windows.public class TaskProcessBuilder {    public static void main(String[] args) {        try {            ProcessBuilder proc = new ProcessBuilder("notepad.exe", "testfile");            proc.start();        } catch (Exception e) {            System.out.println("Ошибка запуска программы");        }    }}